# Logistic Regression Model - Factors Associated with Waitlisting
- Evaluation Outcome Stratified by Site 


::: {.cell}

```{.r .cell-code}
out <- local({
  data <- pins::pin_read(board, 'processed-data')

  data <- data |>
    filter(person_eval == 'yes')

  data <- data |>
    mutate(
      outcome = fct_collapse(
        eval_outcome,
        'Waitlisted' = c('Waitlisted'),
        'Not Waitlisted' = c(
          'Declined',
          'Deferred'
        )
      )
    )

  data <- recipe(
    outcome ~
      age_referral +
        sex +
        site_name +
        bmi_class +
        med_comorbidity_dm +
        med_comorbidity_cad +
        med_comorbidity_htn +
        etiology_mash,
    data = data
  ) |>
    prep() |>
    bake(new_data = NULL)

  pred_methods <- c(
    age_referral = "",
    sex = "logreg",
    site_name = "polyreg",
    bmi_class = "polyreg",
    med_comorbidity_dm = "",
    med_comorbidity_cad = "",
    med_comorbidity_htn = "",
    etiology_mash = "",
    outcome = ""
  )

  set.seed(1)
  m <- mice::mice(data, m = 5, method = pred_methods, printFlag = FALSE)

  fit <- fit.mult.impute(
    outcome ~
      age_referral +
        sex +
        bmi_class +
        site_name * etiology_mash +
        med_comorbidity_dm +
        med_comorbidity_cad +
        med_comorbidity_htn,
    data = data,
    xtrans = m,
    fitter = glm,
    fitargs = list(family = binomial()),
    pr = FALSE
  )

  tbl <- marginaleffects::avg_comparisons(
    fit,
    variables = 'etiology_mash',
    by = 'site_name',
    transform = exp
  )

  tbl |>
    as_tibble() |>
    select(site_name, estimate, conf.low, conf.high, p.value) |>
    gt() |>
    fmt_number(estimate:conf.high, decimals = 2) |>
    fmt(p.value, fns = scales::label_pvalue()) |>
    cols_merge(
      columns = c(estimate, conf.low, conf.high),
      pattern = "{1} ({2}, {3})"
    )
})

out
```

