# Construct Imputation for GLM models


::: {.cell}

```{.r .cell-code}
m <- local({
  data <- pins::pin_read(board, 'processed-data')

  data <- data |>
    filter(person_eval == 'yes')

  data <- data |>
    mutate(
      bmi_class = factor(
        bmi_class,
        levels = c(
          "Normal",
          "Underweight",
          "Overweight",
          "Obese",
          "Morbidly Obese"
        )
      )
    )

  data <- data |>
    mutate(
      outcome = fct_collapse(
        eval_outcome,
        'Waitlisted' = c('Waitlisted'),
        'Not Waitlisted' = c(
          'Declined',
          'Deferred'
        )
      ),
      outcome = case_when(
        outcome == 'Waitlisted' ~ 1,
        outcome == 'Not Waitlisted' ~ 0
      )
    )

  data <- recipe(
    outcome ~
      age_referral +
        sex +
        site_name +
        bmi_class +
        meld_eval +
        total_comorbs +
        etiology_mash,
    data = data
  ) |>
    prep() |>
    bake(new_data = NULL)

  pred_methods <- c(
    age_referral = "",
    sex = "logreg",
    site_name = "polyreg",
    bmi_class = "polyreg",
    meld_eval = "pmm",
    total_comorbs = "",
    etiology_mash = "",
    outcome = ""
  )

  set.seed(1)
  m <- mice::mice(data, m = 5, method = pred_methods, printFlag = FALSE)
})
```

