```{.r .cell-code}
#| label: glm-waitlisting-sex-interaction-marginal-table
#| html-table-processing: none

out <- local({
  fits0 <- with(
    m,
    lme4::glmer(
      outcome ~
        age_referral +
          bmi_class +
          sex +
          etiology_mash +
          meld_eval +
          total_comorbs +
          (1 | site_name),
      family = binomial()
    )
  )

  fits <- with(
    m,
    lme4::glmer(
      outcome ~
        age_referral +
          bmi_class +
          sex * etiology_mash +
          meld_eval +
          total_comorbs +
          (1 | site_name),
      family = binomial()
    )
  )

  lr_test <- D1(fits, fits0)

  p_int <- lr_test$result[, 4]

  p_int <- scales::label_pvalue()(p_int)

  tbl <- marginaleffects::avg_comparisons(
    fits,
    variables = 'sex',
    by = 'etiology_mash',
    transform = exp
  )

  tbl |>
    as_tibble() |>
    select(etiology_mash, estimate, conf.low, conf.high, p.value) |>
    gt() |>
    fmt_number(estimate:conf.high, decimals = 2) |>
    fmt(p.value, fns = scales::label_pvalue()) |>
    cols_merge(
      columns = c(estimate, conf.low, conf.high),
      pattern = "{1} ({2}, {3})"
    ) |>
    tab_footnote(
      footnote = glue::glue('P for interaction = {p_int}')
    )
})
```

